xpath.script:
document.addEventListener('DOMContentLoaded', function() {
    // Hide default portal content
    const portalContent = document.querySelector('#portal_sale_content');
    if (portalContent) {
        portalContent.style.display = 'none';
    }

    // Hide any default tables
    const defaultTables = document.querySelectorAll('section#details table.table');
    defaultTables.forEach(table => {
        if (!table.closest('.custom-quotation-wrapper')) {
            table.style.display = 'none';
        }
    });
});
